mapper.select:
SELECT id, title, region, category, price
FROM BOARD
WHERE id = #{id}

mapper.update:
UPDATE BOARD
SET title = #{title},
	region = #{region},
	category = #{category},
	price = #{price}
WHERE id = #{id}

